import Foundation

// MARK: - Hotel Model
struct Hotel: Identifiable {
    let id = UUID()
    let name: String
    let description: String
    let image: String
    let rating: Double
    let rooms: [Room]
}

// MARK: - Room Model
struct Room: Identifiable {
    let id = UUID()
    let name: String
    let mainPicture: String
    let additionalPictures: [String]
    let price: Double
    let description: String
    let numberOfPeople: Int
    let numberOfBeds: Int
    let floor: Int
}

